import React, { useEffect, useState } from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import DrinkTable from '../components/DrinkTable'

export default function ContentArea() {

    const [drinkData, getData] = useState( [] );
    useEffect( () => {
        fetch( '/api/drinks' ).then( res => res.json() ).then( ( result ) => {
            console.log( result, 'fetch' )
            getData( result )
        } )
    }, [] );
    console.log( drinkData, 'AFTER US' )
    // const updateData = (id) => {
    //     getData(drinkData.filter(item => item.ID !== id))
    // }
    return (

        <div>
            {drinkData.length ? <Container fixed>
                <DrinkTable drinkData={drinkData} />
            </Container> : <h2>loading..</h2>}
        </div>
    );
}
