import React, {useEffect} from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import FilterList from './FilterList'
import { lighten, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';
import FilterListIcon from '@material-ui/icons/FilterList';
import Button from '@material-ui/core/Button';
import Drawer from '@material-ui/core/Drawer';
import {headRows} from '../constants/constants'


const EnhancedTableToolbar = props => {
  const classes = useToolbarStyles();
  const { numSelected, deleteArray, filterResult } = props;

  const [filterShow, filterClick] = React.useState( false );

  const handleFilterClick = ( event ) => {
    filterClick( event.currentTarget );
  }
  const handleFilterClose = () => {
    filterClick( null );
  }


  const handleDeleteClick = ( id ) => {
    fetch( `/api/drinks/`,
      {
        method: 'delete',
        body: JSON.stringify( deleteArray ),
        headers: {
          'Content-Type': 'application/json',
        },
      }
    ).then( res => res.json() ).then( ( result ) => {
      console.log( result )
    } )
  }

  return (
    <Toolbar
      className={clsx( classes.root, {
        [classes.highlight]: numSelected > 0,
      } )}
    >
      <div className={classes.title}>
        {numSelected > 0 ? (
          <Typography color="inherit" variant="subtitle1">
            {numSelected} selected
          </Typography>
        ) : (
            <Typography variant="h6" id="tableTitle">
              Beverage Managment
          </Typography>
          )}

      </div>
      <div className={classes.spacer} />
      <div className={classes.actions}>
        {numSelected > 0 ? (
          <Tooltip title="Delete">
            <IconButton onClick={() => handleDeleteClick( numSelected )} aria-label="delete">
              <DeleteIcon />
            </IconButton>
          </Tooltip>
        ) : (
            <div>
              <Tooltip title="Filter list">
                <IconButton aria-label="filter list" onClick={handleFilterClick}>
                  <FilterListIcon />
                </IconButton>

              </Tooltip>
              <Drawer anchor="right" open={Boolean( filterShow )} onClose={handleFilterClose}>
                <FilterList filterResult={filterResult} />
              </Drawer>
            </div>
          )}
      </div>
    </Toolbar>
  );
};

EnhancedTableToolbar.propTypes = {
  numSelected: PropTypes.number.isRequired,
};

export default EnhancedTableToolbar
